mapper.select:
SELECT MEMBER_CODE
FROM member_info
WHERE MEMBER_CODE = #{memberId}

mapper.insert:
INSERT INTO member_info (
                            MEMBER_CODE
                        ,   MEMBER_NAME
                        ,   BIRTH_DATE
                        ,   CATEGORY_CODE
                        ,   INTRODUCE
                        ,   CONTACT
                        ,   START_DATE
                        ,   ACTIVE_STATUS
                        )
VALUES
                        (
                            #{memberCode}
                        ,   #{memberName}
                        ,   #{birthDate}
                        ,   #{categoryCode}
                        ,   #{introduce}
                        ,   #{contact}
                        ,   #{startDate}
                        ,   #{activeStatus}

                        )

mapper.update:
UPDATE
    member_info


        MEMBER_NAME = #{memberName}


        , BIRTH_DATE = #{birthDate}


        , CATEGORY_CODE = #{categoryCode}


        , INTRODUCE = #{introduce}


        , CONTACT = #{contact}


        , START_DATE = #{startDate}


        , ACTIVE_STATUS = #{activeStatus}


WHERE MEMBER_CODE = #{ memberCode }

mapper.delete:
DELETE
FROM member_info
WHERE MEMBER_CODE = #{ memberId }
